import React, { useState } from "react";
import "./ContentTodo.css";
import dummy from "../static/dummyData";

const ContentTodo = ({todo, idx}) => {
  const [datas, setDatas] = useState(dummy);

  //setDatas - todo 제거, 생성

  return (
    <div>
      <ul className="todoLists">
        <li className="todoList">
          {/* 각 리스트 항목들 */}
          <li className="todo" id={todo.id}>
          <div className="todoDate">{todo.d_day}</div>
          <div className="todoContent">
            {todo.content}
            todo 내용
            <span className="todoContent_checkbox">
              {/*체크박스영역*/}
              <input type="checkbox">{todo.check}</input>
            </span>
            <span className="todoContent_delete">
              {/*삭제영역*/}
              <button className="todoContent_deleteButton">
                {/*삭제버튼*/}
                <i className="far fa-trash-alt"></i>
              </button>
            </span>
          </div>
          {datas.map((todo) => {
            return (
              <li className="todoList">
                {/* 각 리스트 항목들 */}
                <div className="todoDate">{/*D-day표시*/}</div>
                <div className="todoContent">
                  {/*todo내용표시*/}

                  {todo.content}

                  <span className="todoContent_checkbox">
                    {/*체크박스영역*/}
                    <input type="checkbox">{/*체크박스*/}</input>
                  </span>
                  <span className="todoContent_delete">
                    {/*삭제영역*/}
                    <button className="todoContent_deleteButton">
                      {/*삭제버튼*/}
                      <i className="far fa-trash-alt"></i>
                    </button>
                  </span>
                </div>
              </li>
            );
          })}
          </li>
        </li>
      </ul>
    </div>
  );
};

export default ContentTodo;
